var fs = require('fs');
var path = require('path');
describe("Hello World", function() {
  it("should contain index.js file", function () {
    var exists = fs.existsSync(path.join(process.cwd(), "index.js"));
    expect(exists).toBeTruthy();

  });
  it("should log \"Ahoj světe\"", function () {
    spyOn(console, 'log').and.callThrough();
    var myCode = fs.readFileSync(path.join(process.cwd(), "index.js"),'utf-8');
    eval(myCode);
    expect(console.log).toHaveBeenCalledWith("Ahoj světe");
  });
});
