var fs = require('fs');
var path = require('path');
describe("Hello World", function() {
  it("should log \"Petr\"", function () {
    spyOn(console, 'log').and.callThrough();
    var myCode = fs.readFileSync(path.join(process.cwd(), "index.js"),'utf-8');
    eval(myCode);
    expect(console.log).toHaveBeenCalledWith("Petr");
  });
});
